package com.android.onexpay.alipayinternal class AliPayResult(rawResult: Map<String, String?>?) {    var resultStatus: String? = null    var result: String? = null    var memo: String? = null    override fun toString(): String {        return ("resultStatus={${resultStatus}};memo={${memo}};result={${result}}")    }    init {        //{resultStatus=6001, result=null, memo=操作已经取消。}        rawResult?.forEach {  entry ->            when(entry.key){                "resultStatus" -> resultStatus = entry.value                "result" -> result = entry.value                "memo" -> memo = entry.value                else -> {}            }        }    }}